CREATE TABLE ITEM (NAME VARCHAR(255) NOT NULL, PRICE FLOAT, QUANTITY INTEGER, PRIMARY KEY (NAME))
CREATE TABLE books (ID INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, AUTHOR VARCHAR(255) NOT NULL, PAGES INTEGER, PRICE DECIMAL(10,2), PUBLISHEDAT DATE, STOCKSTATUS VARCHAR(255), TITLE VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE authors (ID INTEGER NOT NULL, BIRTHDATE DATE, NAME VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE books_authors (Book_ID INTEGER NOT NULL, authors_ID INTEGER NOT NULL, PRIMARY KEY (Book_ID, authors_ID))
CREATE TABLE authors_books (Author_ID INTEGER NOT NULL, books_ID INTEGER NOT NULL, PRIMARY KEY (Author_ID, books_ID))
ALTER TABLE books_authors ADD CONSTRAINT booksauthorsBookID FOREIGN KEY (Book_ID) REFERENCES books (ID)
ALTER TABLE books_authors ADD CONSTRAINT boksauthorsthorsID FOREIGN KEY (authors_ID) REFERENCES authors (ID)
ALTER TABLE authors_books ADD CONSTRAINT uthorsbooksuthorID FOREIGN KEY (Author_ID) REFERENCES authors (ID)
ALTER TABLE authors_books ADD CONSTRAINT uthorsbooksbooksID FOREIGN KEY (books_ID) REFERENCES books (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(15), PRIMARY KEY (SEQ_NAME))
